package carreau;

import enumeration.Couleur;

public class CarreauCouleur extends Carreau {
	private Couleur couleur;
	
	/**
	 * Constructeur de CarreauCouleur
	 * @param haut : Hauteur du carreau
	 * @param largeur : Largeur du carreau
	 * @param symb : Symbole/Charactère du carreau
	 * @param couleur : Couleur du Carreau
	 */
	public CarreauCouleur(int haut, int largeur, char symb, Couleur c) {
		super(haut, largeur, symb);
		this.couleur = c;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
	}
	
	/**
	 * Renvoie la couleur du Carreau
	 * @return couleur
	 */
	public Couleur getCouleur() {
		return couleur;
	}
}
